let totalSlides=document.querySelectorAll(".slider-img").length,currentSlide=0,sliderWidth=document.querySelector(".slider").clientWidth;function goPrev(){--currentSlide<0&&(currentSlide=totalSlides-1),updateMargin()}function goNext(){++currentSlide>totalSlides-1&&(currentSlide=0),updateMargin()}function updateMargin(){let a=document.querySelector(".slider-img").clientWidth;document.querySelector(".slider-width").style.marginLeft=`-${currentSlide*a}px`}document.querySelector(".slider-width").style.width=`${sliderWidth*totalSlides}px`,document.querySelector(".slider-controls").style.height=`${document.querySelector(".slider").clientHeight}px`,document.querySelector(".slider-controls").style.width=`${sliderWidth}px`,setInterval(goNext,3e3)